import {useState} from 'react'

const Input = (props) => {
  let emptyGig = { Title: '', Location: '', Date: '', Notes: ''}
  const [gig, setGig] = useState(emptyGig)

  const handleChange = (event) => {
    setGig({...gig, [event.target.name]: event.target.value})
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    props.handleCreate(gig)
    setGig(emptyGig)
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label htmlFor="gig">Gig: </label>
        <input type="text" name="gig" onChange={handleChange}/>
        <br />
        <br />
        <label htmlFor="location">Location: </label>
        <input type="text" name="location" onChange={handleChange}/>
        <br />
        <br />
        <label htmlFor="date">Date: </label>
        <input type="text" name="date" onChange={handleChange}/>
        <br />
        <br />
        <input type="submit" />
      </form>
    </>
  )
}

export default Input
